mapper.select:
 	SELECT A.P_NO, A.P_NAME, A.P_POSITION, A.P_AGE, A.P_SAL, A.P_STAT, A.P_HDATE, A.LR, A.GAME, A.MEM_ID, A.P_IMG1, A.P_IMG2 
 	FROM PLAYER A
	WHERE  A.P_NO = #{p_no}

SELECT A.p_no, B.wins, B.loses, B.era, B.ip, B.phr, B.pso, B.phits, B.pbb, B.hold, B.saves
FROM PITCHER B, PLAYER A 
WHERE B.p_no = #{p_no}
